import axios from 'axios';

const URL_STUDENT = "http://localhost:8080/students";

export const getAllStudents = async (name = "", startPoint = "", endPoint = "") => {
    try {
        let query = `${URL_STUDENT}?`;

        // Thêm điều kiện tìm kiếm theo tên (name)
        if (name) {
            query += `name_like=${name}&`;
        }

        // Chỉ thêm điều kiện lọc theo điểm nếu cả startPoint và endPoint đều có giá trị
        if (startPoint !== "" && endPoint !== "") {
            query += `point_gte=${Number(startPoint)}&point_lte=${Number(endPoint)}&`;
        }

        // Loại bỏ ký tự '&' cuối cùng nếu có
        query = query.slice(0, -1);

        let res = await axios.get(query);
        return res.data;
    } catch (e) {
        console.error("Error fetching students:", e);
        return [];
    }
};




export const saveStudent = async (student) => {
    try {
        await axios.post(URL_STUDENT, student);
        return true;
    } catch (e) {
        console.error("Error saving student:", e);
        return false;
    }
}

export const updateStudent = async (student) => {
    try {
        await axios.put(`${URL_STUDENT}/${student.id}`, student);
        return true;
    } catch (e) {
        console.error("Error updating student:", e);
        return false;
    }
}

export const deleteStudent = async (id) => {
    try {
        await axios.delete(`${URL_STUDENT}/${id}`);
        return true;
    } catch (e) {
        console.error("Error deleting student:", e);
        return false;
    }
}
