import {useState} from "react";
import {Link} from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import {toast} from "react-toastify";

function StudentListFunction({ students, setStudents }) {
    const [name, setName] = useState("");

    const deleteStudent = (id) => {
        const updatedStudents = students.filter(s => s.id !== id);
        setStudents(updatedStudents);
        toast.success("Xoá học sinh thành công");
    };

    return (
        <>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="form-control mb-3 w-50 mx-auto"
                placeholder="Search by name..."
            />
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {
                    students
                        .filter(item => item.name.includes(name))
                        .map((item, index) =>
                            <tr key={item.id}>
                                <td>{index + 1}</td>
                                <td>{item.name}</td>
                                <td>{item.address}</td>
                                <td>{item.point}</td>
                                <td>
                                    <Link to={`/edit/${item.id}`} className="btn btn-warning btn-sm me-2">Edit</Link>
                                    <button onClick={() => deleteStudent(item.id)} className="btn btn-danger btn-sm">Delete</button>
                                </td>
                            </tr>
                        )
                }
                </tbody>
            </table>
        </>
    );
}

export default StudentListFunction;
