import React, {useState} from "react";
import {ErrorMessage, Field, Form, Formik} from "formik";
import * as Yup from "yup";
import {toast} from "react-toastify";
import {useNavigate} from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

function StudentCreate({addStudent}) {
    const navigate = useNavigate();

    const objectValid = {
        id: Yup.number().required("Id không được để trống")
            .min(0, "id không được nhỏ hơn 0")
            .max(1000000000000, "Id không được lớn hơn 1000000000000"),
        name: Yup.string().required("Tên không được để trống")
            .min(3, "Tên không được ngắn hơn 3 ký tự")
    };

    const saveStudent = (value) => {
        addStudent(value);  // Add student to the list
        toast.success("Thêm mới thành công");
        navigate("/student");  // Navigate back to student list
    }

    return (
        <>
            <Formik
                initialValues={{id: "", name: "", address: "", point: 0}}
                onSubmit={saveStudent}
                validationSchema={Yup.object(objectValid)}
            >
                <Form className="form-horizontal p-4 bg-light border rounded mx-auto" style={{ maxWidth: "400px" }}>
                    <div className="mb-3">
                        <label htmlFor="id" className="form-label">Id:</label>
                        <Field name="id" className="form-control"/>
                        <ErrorMessage name="id" component="p" className="text-danger"/>
                    </div>

                    <div className="mb-3">
                        <label htmlFor="name" className="form-label">Name:</label>
                        <Field name="name" className="form-control"/>
                        <ErrorMessage name="name" component="p" className="text-danger"/>
                    </div>

                    <div className="mb-3">
                        <label htmlFor="address" className="form-label">Address:</label>
                        <Field name="address" className="form-control"/>
                    </div>

                    <div className="mb-3">
                        <label htmlFor="point" className="form-label">Point:</label>
                        <Field name="point" className="form-control"/>
                    </div>

                    <button type="submit" className="btn btn-primary w-50 mx-auto d-block">Thêm mới</button>
                </Form>
            </Formik>
        </>
    );
}

export default StudentCreate;
