import {useState} from 'react'
import {Field, Form, Formik} from "formik";

function StudentCreate(){
    const [form, setForm] = useState({
        id:"",
        name:"",
        address:"",
        point:"",
    })

    const saveStudent = (value)=>{
        // e.preventDefault()
        console.log(value)
    }
    return (
        <>
            <Formik initialValues={form} onSubmit={saveStudent}>
                    <Form>
                        Id: <Field name="id"/>
                    </Form>
            </Formik>
            <div>
                ID: <input onChange={(e) => setForm({...form, id: e.target.value})}/>
                Name: <input onChange={(e) => setForm({...form, name: e.target.value})}/>
                Address: <input onChange={(e) => setForm({...form, address: e.target.value})}/>
                Point: <input onChange={(e) => setForm({...form, point: e.target.value})}/>
                <button onClick={saveStudent}>Create</button>
            </div>
        </>
    )
}

export default StudentCreate;
