import React, {useState} from "react";
import {BrowserRouter, NavLink, Route, Routes, useNavigate} from "react-router-dom";
import StudentCreate from "./component/student/StudentCreate";
import StudentListFunction from "./component/student/StudentListFunction";
import {ToastContainer} from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [students, setStudents] = useState([
        {id: 1, name: "HaiTT1", address: "Quảng Nam", point: 9},
        {id: 2, name: "HaiTT2", address: "Quảng Nam", point: 9},
        {id: 3, name: "HaiTT3", address: "Quảng Nam", point: 9},
    ]);

    const addStudent = (student) => {
        setStudents([...students, student]);
    }

    return (
        <>
            <BrowserRouter>
                <nav className="navbar navbar-expand-lg navbar-light bg-light p-3">
                    <div className="container-fluid">
                        <NavLink to="/student" className="navbar-brand">Danh sách</NavLink>
                        <NavLink to="/create" className="navbar-brand">Thêm mới</NavLink>
                    </div>
                </nav>
                <div className="container mt-4">
                    <Routes>
                        <Route path="/create" element={<StudentCreate addStudent={addStudent} />} />
                        <Route path="/student" element={<StudentListFunction students={students} />} />
                    </Routes>
                </div>
            </BrowserRouter>
            <ToastContainer />
        </>
    );
}

export default App;
