import {useEffect, useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

function StudentListFunction() {
    const [students, setStudents] = useState([]);
    const [name, setName] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const list = [
            {id: 1, name: "HaiTT1", address: "Quảng Nam", point: 9},
            {id: 2, name: "HaiTT2", address: "Quảng Nam", point: 9},
            {id: 3, name: "HaiTT3", address: "Quảng Nam", point: 9}
        ];
        const temp = list.filter(temp => temp.name.includes(name));
        setStudents(temp);
    }, [name]);

    const handleDelete = (id) => {
        const updatedStudents = students.filter(student => student.id !== id);
        setStudents(updatedStudents);
    };

    return (
        <>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="form-control mb-3 w-50 mx-auto"
                placeholder="Search by name..."
            />
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {students.map((item, index) => (
                    <tr key={item.id}>
                        <td>{index + 1}</td>
                        <td>{item.name}</td>
                        <td>{item.address}</td>
                        <td>{item.point}</td>
                        <td>
                            <button className="btn btn-info btn-sm me-2" onClick={() => navigate(`/view/${item.id}`)}>View</button>
                            <button className="btn btn-warning btn-sm me-2" onClick={() => navigate(`/edit/${item.id}`)}>Edit</button>
                            <button className="btn btn-danger btn-sm" onClick={() => handleDelete(item.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </>
    );
}

export default StudentListFunction;
