import React, {useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

function StudentListFunction({students}) {
    const [name, setName] = useState("");

    const filteredStudents = students.filter(student => student.name.includes(name));

    return (
        <>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="form-control mb-3 w-50 mx-auto"
                placeholder="Search by name..."
            />
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                </tr>
                </thead>
                <tbody>
                {
                    filteredStudents.map((item, index) =>
                        <tr key={item.id}>
                            <td>{index + 1}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.point}</td>
                        </tr>
                    )
                }
                </tbody>
            </table>
        </>
    )
}

export default StudentListFunction;
