import {useEffect, useState} from "react";
import { Link } from "react-router-dom";
import { Modal, Button, Table, Form } from 'react-bootstrap';
import { toast } from "react-toastify";
import 'bootstrap/dist/css/bootstrap.min.css';
import * as studentService from "../../service/StudentService";


function StudentListFunction() {
    const [students, setStudents] = useState([]);
    const [name, setName] = useState("");

    useEffect (() => {
        //     Call API search name
        getAllStudents(name)
    }, [name])

    useEffect(() => {
        return () => {
            //clean up <=> componentWillUnmount
        }
    }, [])

    const getAllStudents = async (name) => {
        let res = await studentService.getAllStudents(name);
        setStudents(res);
    }
    const deleteStudent = (id) => {
        const updatedStudents = students.filter(s => s.id !== id);
        setStudents(updatedStudents);
        toast.success('Xoá học sinh thành công');
        setShowModal(false);
    };

    const handleShow = (student) => {
        setSelectedStudent(student);
        setShowModal(true);
    };

    return (
        <>
            <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="mb-3 w-50 mx-auto"
                placeholder="Search by name..."
            />
            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {
                    students
                        .filter(item => item.name.includes(name))
                        .map((item, index) =>
                            <tr key={item.id}>
                                <td>{index + 1}</td>
                                <td>{item.name}</td>
                                <td>{item.address}</td>
                                <td>{item.point}</td>
                                <td>
                                    <Link to={`/edit/${item.id}`} className="btn btn-warning btn-sm me-2">Edit</Link>
                                    <Button
                                        variant="danger"
                                        size="sm"
                                        onClick={() => handleShow(item)}>
                                        Delete
                                    </Button>
                                </td>
                            </tr>
                        )
                }
                </tbody>
            </Table>

            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Xác nhận xoá</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Bạn có chắc chắn muốn xoá học sinh "{selectedStudent?.name}" không?
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Huỷ
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => deleteStudent(selectedStudent.id)}>
                        Xoá
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default StudentListFunction;
