import { Table, Form, Button, Modal } from 'react-bootstrap';
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import * as studentService from "../../service/StudentService";

function StudentListFunction({ students, setStudents }) {
    const [name, setName] = useState("");
    const [pointMin, setPointMin] = useState("");
    const [pointMax, setPointMax] = useState("");
    const [showModal, setShowModal] = useState(false);
    const [selectedStudent, setSelectedStudent] = useState(null);

    useEffect(() => {
        getAllStudents(name, pointMin, pointMax);
    }, [name, pointMin, pointMax]);

    const getAllStudents = async (name, pointMin, pointMax) => {
        let res = await studentService.getAllStudents(name, pointMin, pointMax);
        setStudents(res);
    };

    const deleteStudent = async (id) => {
        try {
            await studentService.deleteStudent(id);
            setStudents(students.filter(student => student.id !== id));
            setShowModal(false);
            toast.success("Xóa học sinh thành công!");
        } catch (error) {
            toast.error("Không thể xóa học sinh.");
        }
    };

    const handleShow = (student) => {
        setSelectedStudent(student);
        setShowModal(true);
    };

    return (
        <>
            <Form className="d-flex justify-content-center mb-3">
                <Form.Control
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="me-2"
                    placeholder="Tìm kiếm theo tên..."
                />
                <Form.Control
                    type="number"
                    value={pointMin}
                    onChange={(e) => setPointMin(e.target.value)}
                    className="me-2"
                    placeholder="Điểm tối thiểu..."
                />
                <Form.Control
                    type="number"
                    value={pointMax}
                    onChange={(e) => setPointMax(e.target.value)}
                    className="me-2"
                    placeholder="Điểm tối đa..."
                />
                <Button onClick={() => getAllStudents(name, pointMin, pointMax)}>Tìm kiếm</Button>
            </Form>

            <div className="container mt-4">
                <Link to={`/create`} className="btn btn-primary btn-sm mb-3">Tạo mới</Link>

                <Table striped bordered hover variant="dark">
                    <thead>
                    <tr>
                        <th>STT</th>
                        <th>Tên</th>
                        <th>Địa chỉ</th>
                        <th>Điểm</th>
                        <th>Hành động</th>
                    </tr>
                    </thead>
                    <tbody>
                    {students.map((item, index) => (
                        <tr key={item.id}>
                            <td>{index + 1}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.point}</td>
                            <td>
                                <Link to={`/edit/${item.id}`} className="btn btn-warning btn-sm me-2">Chỉnh sửa</Link>
                                <Button
                                    variant="danger"
                                    size="sm"
                                    onClick={() => handleShow(item)}>
                                    Xóa
                                </Button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
            </div>

            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Xác nhận xóa</Modal.Title>
                </Modal.Header>
                <Modal.Body>Bạn có chắc muốn xóa học sinh {selectedStudent?.name} không?</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Hủy
                    </Button>
                    <Button variant="danger" onClick={() => deleteStudent(selectedStudent.id)}>
                        Xóa
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default StudentListFunction;
