import { useState } from "react";
import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import { toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

function StudentListFunction({ students, setStudents }) {
    const [name, setName] = useState("");
    const [selectedStudent, setSelectedStudent] = useState(null);

    const deleteStudent = (id) => {
        const updatedStudents = students.filter(s => s.id !== id);
        setStudents(updatedStudents);
        toast.success("Xoá học sinh thành công");
        setSelectedStudent(null);
    };

    return (
        <>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="form-control mb-3 w-50 mx-auto"
                placeholder="Search by name..."
            />
            <table className="table table-striped">
                <thead>
                <tr>
                    <th>STT</th>
                    <th>Name</th>
                    <th>Address</th>
                    <th>Point</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {
                    students
                        .filter(item => item.name.includes(name))
                        .map((item, index) =>
                            <tr key={item.id}>
                                <td>{index + 1}</td>
                                <td>{item.name}</td>
                                <td>{item.address}</td>
                                <td>{item.point}</td>
                                <td>
                                    <Link to={`/edit/${item.id}`} className="btn btn-warning btn-sm me-2">Edit</Link>
                                    <button
                                        onClick={() => setSelectedStudent(item)}
                                        className="btn btn-danger btn-sm"
                                        data-bs-toggle="modal"
                                        data-bs-target="#deleteModal">
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        )
                }
                </tbody>
            </table>

            <div className="modal fade" id="deleteModal" tabIndex="-1" aria-labelledby="deleteModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="deleteModalLabel">Xác nhận xoá</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            Bạn có chắc chắn muốn xoá học sinh "{selectedStudent?.name}" không?
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Huỷ</button>
                            <button
                                type="button"
                                className="btn btn-danger"
                                onClick={() => {
                                    deleteStudent(selectedStudent.id);
                                    document.getElementById('deleteModal').click();
                                }}>
                                Xoá
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default StudentListFunction;
